const  getCookie = (cookieName)=>{
    return document.cookie
    .split(";")
    .find((token)=>token.trim().split("=")[0]=== cookieName)
    ?.split("=")[1]
}

export{getCookie}

const setCookie = (tokens) => {
  document.cookie = `accessToken=${tokens.accessToken}; max-age=${
    1 * 24 * 60 * 60
  }`;
